// ***************** SoundTask ****************
// Outputs the 8bit input into the DAC until sound is finished
// Inputs:  none
// Outputs: none
void SoundTask(void){
	
	if(Length){
		DAC_Out(((*SoundPt) * 8) / 11); //Assuming 6bit Dac, 8 Bit input
		SoundPt++;
		Length --;
		if(Length == 1){
			Music_Resume();
		}
	}
	else if(duration && songPt){
		DAC_Out(wave[idx]);
		idx = ((idx+1)% 64);
	} 
	else {

		//NVIC_DIS0_R = 1<<23; // Disable IRQ 19 in NVIC
		TIMER2_CTL_R = 0x00000000;   // 1) disable timer2A
	} 
	
}
	
void (*PeriodicTask2)(void);   // user function

// ***************** Sound_Init ****************
// Activate TIMER2 interrupts to run user task periodically
// Activate DAC 
// Inputs:  none
// Outputs: none
void Sound_Init(void){
// For Sound effects
	unsigned long volatile delay;
	Length = 0;
	DAC_Init();
  SYSCTL_RCGCTIMER_R |= 0x04;   // 0) activate timer2
	delay = SYSCTL_RCGCTIMER_R;
	PeriodicTask2 = SoundTask;          // user function
  TIMER2_CTL_R = 0x00000000;   // 1) disable timer2A
  TIMER2_CFG_R = 0x00000000;   // 2) 32-bit mode
  TIMER2_TAMR_R = 0x00000002;  // 3) periodic mode
	TIMER2_TAILR_R = (80000000/11000)-1;   // 4) reload value 
	//TIMER2_TAILR_R = 500;
  TIMER2_TAPR_R = 0;           // 5) clock resolution
  TIMER2_ICR_R = 0x00000001;   // 6) clear timeout flag
  TIMER2_IMR_R = 0x00000001;   // 7) arm timeout
  NVIC_PRI5_R = (NVIC_PRI5_R&0x00FFFFFF) | 0x10000000;; // 8) priority 0
  NVIC_EN0_R = 1<<23;          // 9) enable IRQ 23 in
}; 


